import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as o,a as e,b as n,d as s,e as i,r as l}from"./app.59944050.js";const h={},d=i('<h1 id="测试" tabindex="-1"><a class="header-anchor" href="#测试" aria-hidden="true">#</a> 测试</h1><h2 id="a-b-test" tabindex="-1"><a class="header-anchor" href="#a-b-test" aria-hidden="true">#</a> A/B TEST</h2><h3 id="一、定义" tabindex="-1"><a class="header-anchor" href="#一、定义" aria-hidden="true">#</a> 一、定义</h3><blockquote><p>在同一时间对目标受众做<strong>科学抽样</strong>、<strong>分组测试</strong>以评估效果</p></blockquote><h3 id="二、原因" tabindex="-1"><a class="header-anchor" href="#二、原因" aria-hidden="true">#</a> 二、原因</h3><ol><li>**风险控制。**小流量实验可以避免直接上线效果不好造成损失。其次，实验迭代的过程中，决策都是有科学依据的，可以避免系统性的偏差。</li><li><strong>因果推断</strong>。最好的因果推断工具</li><li><strong>复利效应</strong>。</li></ol><h3 id="三、分析实践" tabindex="-1"><a class="header-anchor" href="#三、分析实践" aria-hidden="true">#</a> 三、分析实践</h3><ol><li><p><strong>确定业务的指标体系</strong>：可以从宏观/微观、长期/短期、横向/纵向三个角度建设指标体系。</p></li><li><p><strong>分类检验</strong>：对指标进行置信度计算的时候，并不会每次都用同一套方法，而是针对不同的指标类型（包括转化类、人均类、CTR 类等）进行不同的建模采用不同的方法。</p></li><li><p><strong>统计修正</strong>：如果一个实验开了多个组，可能犯了多重比较的错误。还有时开完实验之后每天都会查看结果，这就犯了连续观测的错误。所以在实践中需要有一些统计修正的方法来修正行为。</p></li><li><p><strong>基于叶贝斯体系的探索</strong>：区别于经典的假设检验，我们也在探索基于叶贝斯体系，如何评估实验效果，降低面向用户使用时候的理解门槛。在智能流量调优、模型超参数搜索等场景下有具体落地。</p></li></ol><h3 id="四、资料" tabindex="-1"><a class="header-anchor" href="#四、资料" aria-hidden="true">#</a> 四、资料</h3>',9),c={href:"https://mp.weixin.qq.com/s/45q4vrZQGLhFq6EmTypFMA",target:"_blank",rel:"noopener noreferrer"};function _(p,g){const r=l("ExternalLinkIcon");return t(),o("div",null,[d,e("ol",null,[e("li",null,[e("a",c,[n("火山引擎 A/B 测试的思考与实践"),s(r)])])])])}const m=a(h,[["render",_],["__file","A or B test.html.vue"]]);export{m as default};
